import React, { PureComponent } from "react";
import PropTypes from "prop-types";
import { withStyles } from "@material-ui/core/styles";
import Input from "@material-ui/core/Input";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import InputLabel from "@material-ui/core/InputLabel";
import TextField from '@material-ui/core/TextField';
import {GridList,ListSubheader,GridListTile,GridListTileBar,IconButton} from '@material-ui/core';
//import InfoIcon from '@material-ui/icons/Info';
import { fetchToDos } from './Action/dataAction'
import { bindActionCreators, compose } from 'redux';
import { connect } from 'react-redux';

let pQry='';

const styles = theme => ({
  root: {
    display: "flex",
    flexWrap: "wrap"
  },
  formControl: {
    margin: theme.spacing.unit,
    minWidth: 120
  },
  selectEmpty: {
    marginTop: theme.spacing.unit * 2
  },
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    justifyContent: 'space-around',
    overflow: 'hidden',
    backgroundColor: theme.palette.background.paper,
  },
  gridList: {
    width: 500,
    height: 450,
  },
  icon: {
    color: 'rgba(255, 255, 255, 0.54)',
  },
});

class Searcher extends PureComponent{
  state = {
    selected: null,
    hasError: false,
    searchPitem:''
  };

  callApis = (value) => {
    if(value.length > 3){
      if(this.props.todos.datas.items.length>0){
        this.props.todos.datas.items = this.props.todos.datas.items.filter((item)=>{
            return item.owner.login.startsWith(value);
        })
      }else{
        this.props.fetchToDos(this.state.selected,value)
      }
    this.setState({searchPitem: value});
    }
  }

  // handleChange = (value) => {
  //   if(this.state.searchPitem.length > 3){
  //     this.setState({searchPitem: ''});
  //   }
  //   this.setState({selected:value})
  // }

  render() {
    const { classes } = this.props;
    const { selected, hasError } = this.state;
    const { datas, isFetching, error, dataFetched } = this.props.todos;
    //
    return (
      <div style={{display: 'flex', alignItems: 'center',justifyContent: 'center',}}> 

        <TextField id="standard-search" 
        label="Start typing to search" 
        type="search" 
        variant="filled"
        defaultValue={this.state.searchPitem}
        onChange={event => this.callApis(event.target.value)}/>
        <FormControl variant="filled" className={classes.formControl}>
        <InputLabel htmlFor="filled-age-native-simple">Type</InputLabel>
        <Select
          native
          value={selected}
          onChange={event => this.handleChange(event.target.value)}
          inputProps={{
            id: 'filled-age-native-simple',
          }}
        >
          <option aria-label="None" value="" />
          <option value={"users"}>Users</option>
          <option value={"repositories"}>Repositories</option>
        </Select>
      </FormControl>

      {
        isFetching && <p>Loading...</p>
      }
      {datas.items != undefined ?(
      <div className={classes.root}>
      <GridList cellHeight={180} className={classes.gridList}>
        <GridListTile key="Subheader" cols={2} style={{ height: 'auto' }}>
        </GridListTile>
        {datas.items.map((tile) => (
          <GridListTile key={tile.owner.avatar_url}>
            <img src={tile.owner.avatar_url} alt={tile.owner.login} />
            <GridListTileBar
              title={tile.owner.login}
              subtitle={<span>by: {tile.owner.login}</span>}
              actionIcon={
                <IconButton aria-label={`info about ${tile.owner.login}`} className={classes.icon}>
                  {/* <InfoIcon /> */}
                </IconButton>
              }
            />
          </GridListTile>
        ))}
      </GridList>
    </div>):null}
      </div>
    );
  }
}

Searcher.propTypes = {
  classes: PropTypes.object.isRequired
};

function mapStateToProps(state) {
  return {
      todos: state.DataReducer
  }
}

function mapDispatchToProps(dispatch) {
  return {
      ...bindActionCreators({ fetchToDos }, dispatch)
  }
}

export default compose(withStyles(styles),connect(mapStateToProps, mapDispatchToProps))(Searcher);
