import { FETCH_TODOS_SUCCESS, FETCH_TODOS_FAILURE, FETCHING_TODOS } from '../Constant';

const initialState = {
    datas: [],
    isFetching: false,
    status: false,
    error: false
}

export default function todosReducer(state = initialState, action) {
debugger;
    switch(action.type) {
        case FETCHING_TODOS:
            return {
                ...state,
                isFetching: true,
                status: false
            }
        case FETCH_TODOS_SUCCESS:
            return {
                ...state,
                isFetching: false,
                status: true,
                datas: action.data
            }
        case FETCH_TODOS_FAILURE:
            return {
                ...state,
                isFetching: false,
                status: false,
                error: true
            }
        default:
            return state
    }
}
